'''This package sets up the admin interface for the personnel app.'''from django.contrib import adminfrom personnel.models import Person, Role, JobType, Organizationclass PersonAdmin(admin.ModelAdmin):    passadmin.site.register(Person, PersonAdmin)class RoleAdmin(admin.ModelAdmin):    passadmin.site.register(Role, RoleAdmin)class JobTypeAdmin(admin.ModelAdmin):    passadmin.site.register(JobType, JobTypeAdmin)class OrganizationAdmin(admin.ModelAdmin):    passadmin.site.register(Organization, OrganizationAdmin)